function expno = mdm_bruker_dir2expno(data_path)% function expno = mdm_bruker_dir2expno(data_path)% DirList = dir(data_path);[DirSize1,DirSize2] = size(DirList);expno = [];for ndir = 1:DirSize1    expnotest = str2num(getfield(DirList,{ndir,1},'name'));    if isempty(expnotest) == 0        expno = [expno expnotest];    endendexpno = sort(expno,'ascend');